customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
if (attrs.layerUseVar == 1) {
    var layerStr: string = `[${attrs.layerVarID}-${getTypeDataListName(3, attrs.layerVarID)}]`;
}
else {
    layerStr = attrs.layerID.toString();
}
var str = "图层-" + layerStr + "：";
if (attrs.offsetEnabled) {
    var dxStr = attrs.dxUseVar ? `[${attrs.dxVarID}-${getTypeDataListName(3, attrs.dxVarID)}]` : attrs.dx;
    var dyStr = attrs.dyUseVar ? `[${attrs.dyVarID}-${getTypeDataListName(3, attrs.dyVarID)}]` : attrs.dy;
    str += "\n偏移：" + dxStr + "," + dyStr;
}
if (attrs.scaleEnabled) {
    var scaleXStr = attrs.scaleXUseVar ? `[${attrs.scaleXVarID}-${getTypeDataListName(3, attrs.scaleXVarID)}]` : attrs.scaleX;
    var scaleYStr = attrs.scaleYUseVar ? `[${attrs.scaleYVarID}-${getTypeDataListName(3, attrs.scaleYVarID)}]` : attrs.scaleY;
    str += "\n缩放：" + scaleXStr + "," + scaleYStr;
}
if (attrs.autoMoveEnabled) {
    var xMoveStr = attrs.xMoveUseVar ? `[${attrs.xMoveVarID}-${getTypeDataListName(3, attrs.xMoveVarID)}]` : attrs.xMove;
    var yMoveStr = attrs.yMoveUseVar ? `[${attrs.yMoveVarID}-${getTypeDataListName(3, attrs.yMoveVarID)}]` : attrs.yMove;
    str += "\n自动滚动：" + xMoveStr + "," + yMoveStr;
}
if (attrs.alphaEnabled) {
    var alphaStr = attrs.alphaUseVar ? `[${attrs.alphaVarID}-${getTypeDataListName(3, attrs.alphaVarID)}]` : attrs.alpha;
    str += "\n透明度：" + alphaStr;
}
if (attrs.visibleEnabled) {
    var visibleStr = attrs.visibleUseVar ? `[${attrs.visibleVarID}-${getTypeDataListName(4, attrs.visibleVarID)}]` : attrs.visibleItems[attrs.visible];
    str += "\n是否可见：" + visibleStr;
}
str;

