/**
 * 材质相关的数据
 * -- 单一材质编辑器储存的数据：[{ materials: [] },{ materials: [] }] index表示通道
 */
declare class materialPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了2号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    stages: { materials: any[] }[][]; // 图像层 [第一维-指令][第二维-材质通道]
    uiLayers: { materials: any[] }[][]; // 界面层
    screens: { materials: any[] }[][]; // 全画面
    imagePassages: { passageID: number, materialSetting: { materials: any[] }[] }[]; // 指定的图像
    uis: { uiID: number, materialSetting: { materials: any[] }[] }[]; // 指定的界面
}
var passageType = "gcMaterial";
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // -- 图像层的场合 通道编号大于界面的编号以便 imageLayerPassageStart 时在界面之后
    var passageID = 2000001;
    // -- 获取通道数据
    var passageData: materialPassageData = imageLayerPassageData[passageID];
    // -- 获取储存的通道，没有的话则新建
    if (!passageData) {
        return;
    }
    passageData.passageID = passageID;
    passageData.cmd = cmd;
    passageData.type = passageType;
    // 播放
    if (isPlayMode) {
        if (cmdParams.targetType == 0) {
            removeMaterialFromLayer_playMode(passageData.stages, imageLayer);
        }
        // // -- 指定的图像编号
        else if (cmdParams.targetType == 1) {
            var imagePassageID = cmdParams.passageID;
            var targetDisplayObjectPassage = imageLayerPassageData[imagePassageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) return;
            if (!cmdParams.passageIDUseVar) {
                if (cmdParams.clearType == 1) {
                    // 遍历所有来自指令的添加
                    for (var i = 0; i < passageData.imagePassages.length; i++) {
                        var imagePassageInfos = passageData.imagePassages[i];
                        // 遍历指定的通道
                        var imagePassageInfo = imagePassageInfos.materialSetting[cmdParams.materialPassage];
                        // 剔除该ID的材质
                        var m = ArrayUtils.matchAttributes(imagePassageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                        if (m) {
                            targetDisplayObjectPassage.displayObject.removeMaterial(m, cmdParams.materialPassage);
                        }
                    }
                }
                else {
                    targetDisplayObjectPassage.displayObject.clearMaterials();
                }
            }
        }
        // -- 指定的界面
        else if (cmdParams.targetType == 2) {
            var uiID = cmdParams.uiID;
            var targetPassageID = 1000000 + uiID;
            var targetDisplayObjectPassage = imageLayerPassageData[targetPassageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) return;
            if (!cmdParams.objectUseVar) {
                var uiID = cmdParams.uiID;
                var uiPassageID = cmdParams.uiID + 1000000;
                if (cmdParams.clearType == 1) {
                    // 遍历所有来自指令的添加
                    for (var i = 0; i < passageData.uis.length; i++) {
                        var uiPassageInfos = passageData.uis[i];
                        if (uiPassageInfos.uiID != uiID) continue;
                        // 遍历指定的通道
                        var uiPassageInfo = uiPassageInfos.materialSetting[cmdParams.materialPassage];
                        // 剔除该ID的材质
                        var m = ArrayUtils.matchAttributes(uiPassageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                        if (m) {
                            targetDisplayObjectPassage.displayObject.removeMaterial(m, cmdParams.materialPassage);
                        }
                    }
                }
                else {
                    targetDisplayObjectPassage.displayObject.clearMaterials();
                }
            }
        }
        // -- 界面层
        else if (cmdParams.targetType == 3) {
            removeMaterialFromLayer_playMode(passageData.uiLayers, uiLayer);
        }
        // -- 全画面
        else if (cmdParams.targetType == 4) {
            removeMaterialFromLayer_playMode(passageData.screens, gameLayer);
        }
    }
    else {
        // 将需要增加的材质添加至列表中
        // -- 画面
        if (cmdParams.targetType == 0) {
            removeMaterialFromLayer("stages");
        }
        // -- 指定的图像编号
        else if (cmdParams.targetType == 1) {
            if (!cmdParams.passageIDUseVar) {
                var imagePassageID = cmdParams.passageID;
                if (cmdParams.clearType == 1) {
                    // 遍历所有来自指令的添加
                    for (var i = 0; i < passageData.imagePassages.length; i++) {
                        var imagePassageInfos = passageData.imagePassages[i];
                        if (imagePassageInfos.passageID != imagePassageID) continue;
                        // 遍历指定的通道
                        var imagePassageInfo = imagePassageInfos.materialSetting[cmdParams.materialPassage];
                        // 剔除该ID的材质
                        var m = ArrayUtils.matchAttributes(imagePassageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                        if (m) imagePassageInfo.materials.splice(imagePassageInfo.materials.indexOf(m), 1);
                    }
                }
                else {
                    for (var i = 0; i < passageData.imagePassages.length; i++) {
                        var im = passageData.imagePassages[i];
                        if (im.passageID != imagePassageID) continue;
                        passageData.imagePassages.splice(i, 1);
                        i--;
                    }
                }
            }
        }
        // -- 指定的界面
        else if (cmdParams.targetType == 2) {
            if (!cmdParams.objectUseVar) {
                var uiID = cmdParams.uiID;
                var uiPassageID = uiID + 1000000;
                if (cmdParams.clearType == 1) {
                    // 遍历所有来自指令的添加
                    for (var i = 0; i < passageData.uis.length; i++) {
                        var uiPassageInfos = passageData.uis[i];
                        if (uiPassageInfos.uiID != uiID) continue;
                        // 遍历指定的通道
                        var uiPassageInfo = uiPassageInfos.materialSetting[cmdParams.materialPassage];
                        // 剔除该ID的材质
                        var m = ArrayUtils.matchAttributes(uiPassageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                        if (m) uiPassageInfo.materials.splice(uiPassageInfo.materials.indexOf(m), 1);
                    }
                }
                else {
                    for (var i = 0; i < passageData.uis.length; i++) {
                        var iu = passageData.uis[i];
                        if (iu.uiID != uiID) continue;
                        passageData.uis.splice(i, 1);
                        i--;
                    }
                }
            }
        }
        // -- 界面层
        else if (cmdParams.targetType == 3) {
            removeMaterialFromLayer("uiLayers");
        }
        // -- 全画面
        else if (cmdParams.targetType == 4) {
            removeMaterialFromLayer("screens");
        }
    }
    function removeMaterialFromLayer_playMode(passageDatas: { materials: any[] }[][], layer: GameSprite): void {
        if (cmdParams.clearType == 1) {
            // 遍历所有来自指令的添加
            for (var i = 0; i < passageDatas.length; i++) {
                var stageInfos = passageDatas[i];
                // 遍历指定的通道
                var stageInfo = stageInfos[cmdParams.materialPassage];
                // 剔除该ID的材质
                var m = ArrayUtils.matchAttributes(stageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                if (m) {
                    layer.removeMaterial(m, cmdParams.materialPassage);
                }
            }
        }
        else {
            layer.clearMaterials();
        }
    }
    function removeMaterialFromLayer(attrname: string): void {
        let passageDatas = passageData[attrname];
        if (cmdParams.clearType == 1) {
            // 遍历所有来自指令的添加
            for (var i = 0; i < passageDatas.length; i++) {
                var stageInfos = passageDatas[i];
                // 遍历指定的通道
                var stageInfo = stageInfos[cmdParams.materialPassage];
                // 剔除该ID的材质
                var m = ArrayUtils.matchAttributes(stageInfo.materials, { id: cmdParams.materialID }, true, "==")[0];
                if (m) stageInfo.materials.splice(stageInfo.materials.indexOf(m), 1);
            }
        }
        else {
            passageData[attrname] = [];
        }
    }
}
